mapper.sql:
status = '0'

        AND ooso.id = #{id}


        AND ooso.uid = #{uid}


        AND ooso.oil_station_code = #{oilStationCode}


        AND ooso.operator = #{operator}


        AND ooso.create_time >= date_format(CONCAT(#{createTime}, ' 00:00:00'), '%Y-%m-%d %H:%i:%s')
        AND ooso.create_time <= date_format(CONCAT(#{createTime}, ' 23:59:59'), '%Y-%m-%d %H:%i:%s')


ORDER BY ooso.create_time desc

mapper.delete:
DELETE FROM o_oil_station_operator WHERE 1=1

    AND id = #{id}


    AND uid = #{uid}
