mapper.sql:


        AND nld.id = #{id}


        AND nld.uid = #{uid}


        AND nld.wx_order_id = #{wxOrderId}


        AND nld.luck_draw_code = #{luckDrawCode}


        AND nld.remark like concat('%',#{dicRemark},'%')


        AND nld.status = #{status}


ORDER BY nld.create_time desc

mapper.select:
SELECT nld.id as "id", nld.uid as "uid", nu.open_id as "openId", no.pay_money as "payMoney", nd.dic_remark as "cashBackModeJson" from n_luck_draw nld INNER JOIN n_order no on nld.wx_order_id = no.wx_order_id INNER JOIN n_user nu on nld.uid = nu.id INNER JOIN n_dic nd on nld.luck_draw_code = dic_code where 1=1
nld.uid = #{uid}
ORDER BY nld.create_time asc
LIMIT ${start},${size}

select nu.id as "uid", nu.avatar_url as "avatarUrl", nu.nick_name as "nickName", nld.wx_order_id as "wxOrderId", nld.create_time as "createTime", nld.remark as "luckDrawRemark", no.pay_money as "payMoney", nd.dic_remark as "luckDrawRule", ns.shop_title as "shopTitle", ns.shop_head_img_url as "shopHeadImgUrl", nld.status as "luckDrawStatusCode", lds.dic_name as "luckDrawStatusName" from n_luck_draw nld INNER JOIN n_dic nd ON nld.luck_draw_code=nd.dic_code and dic_type="luckDraw" INNER JOIN n_dic lds ON nld.status=lds.dic_code and lds.dic_type="luckDrawStatus" INNER JOIN n_user nu ON nld.uid=nu.id INNER JOIN n_order no ON no.wx_order_id=nld.wx_order_id INNER JOIN n_shop ns ON ns.id=no.shop_id where 1=1
and nld.status=#{status}
and no.shop_id=#{shopId}
ORDER BY nld.create_time ASC

select COUNT(*) total from n_luck_draw nld INNER JOIN n_dic nd ON nld.luck_draw_code=nd.dic_code and dic_type="luckDraw" INNER JOIN n_dic lds ON nld.status=lds.dic_code and lds.dic_type="luckDrawStatus" INNER JOIN n_user nu ON nld.uid=nu.id INNER JOIN n_order no ON no.wx_order_id=nld.wx_order_id INNER JOIN n_shop ns ON ns.id=no.shop_id where 1=1
and nld.status=#{status}
and ns.id=#{shopId}

select nu.id as "uid", nu.avatar_url as "avatarUrl", nu.nick_name as "nickName", nld.wx_order_id as "wxOrderId", nld.create_time as "createTime", nld.remark as "luckDrawRemark", no.pay_money as "payMoney", nd.dic_remark as "luckDrawRule", ns.id as "shopId", ns.shop_title as "shopTitle", ns.shop_head_img_url as "shopHeadImgUrl", nld.status as "luckDrawStatusCode", lds.dic_name as "luckDrawStatusName" from n_luck_draw nld INNER JOIN n_dic nd ON nld.luck_draw_code=nd.dic_code and dic_type="luckDraw" INNER JOIN n_dic lds ON nld.status=lds.dic_code and lds.dic_type="luckDrawStatus" INNER JOIN n_user nu ON nld.uid=nu.id INNER JOIN n_order no ON no.wx_order_id=nld.wx_order_id INNER JOIN n_shop ns ON ns.id=no.shop_id where 1=1
and nld.status=#{status}
and nld.uid=#{uid}
ORDER BY nld.create_time ASC

select COUNT(*) total from n_luck_draw nld INNER JOIN n_dic nd ON nld.luck_draw_code=nd.dic_code and dic_type="luckDraw" INNER JOIN n_dic lds ON nld.status=lds.dic_code and lds.dic_type="luckDrawStatus" INNER JOIN n_user nu ON nld.uid=nu.id INNER JOIN n_order no ON no.wx_order_id=nld.wx_order_id INNER JOIN n_shop ns ON ns.id=no.shop_id where 1=1
and nld.status=#{status}
and nld.uid=#{uid}
