mapper.select:
select np.id as "productId", np.title as "productTitle", np.head_img_url as "productHeadImgUrl", np.price as "productPrice", no.product_num as "productNum", no.transaction_product_detail as "transactionProductDetail", no.pay_money as "payMoney", no.express_number as "expressNumber", os.dic_name as "orderStatus", no.remark as "remark" from n_order no INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_product np on no.product_id=np.id where
and no.uid = #{uid}
LIMIT ${start},${size}

select np.id as "productId", np.title as "productTitle", np.head_img_url as "productHeadImgUrl", np.price as "productPrice", no.id as "orderId", no.address_id as "addressId", no.product_num as "productNum", no.transaction_product_detail as "transactionProductDetail", no.all_pay_amount as "allPayAmount", no.pay_money as "payMoney", no.use_balance_monney as "useBalanceMonney", no.use_integral_num as "useIntegralNum", no.express_name as "expressName", no.express_number as "expressNumber", no.wx_order_id as "wxOrderId", date_format(no.create_time, '%Y-%c-%d %H:%i:%s') as "createTime", date_format(no.update_time, '%Y-%c-%d %H:%i:%s') as "updateTime", no.remark as "remark", os.dic_code as "orderStatusCode", os.dic_name as "orderStatusName" from n_order no INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_product np on no.product_id=np.id where order_type='purchaseProduct'
and no.id = #{id}
and no.uid = #{uid}
and no.status = #{status}
and express_number != "" and express_number IS NOT NULL
ORDER by no.create_time desc
LIMIT ${start},${size}

SELECT COUNT(*) total from n_order no INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_product np on no.product_id=np.id where order_type='purchaseProduct'
and no.uid = #{uid}
and no.status = #{status}
and express_number != "" and express_number IS NOT NULL

select ns.id as "shopId", ns.shop_title as "shopTitle", ns.shop_head_img_url as "shopHeadImgUrl", nf.id as "foodId", nf.food_title as "foodTitle", nf.food_head_img_url as "foodHeadImgUrl", nf.food_price as "foodPrice", no.id as "orderId", no.foods_id as "foodsId", no.foods_num as "foodsNum", no.transaction_foods_detail as "transactionFoodsDetail", no.all_pay_amount as "allPayAmount", no.pay_money as "payMoney", no.use_balance_monney as "useBalanceMonney", no.use_integral_num as "useIntegralNum", no.wx_order_id as "wxOrderId", date_format(no.create_time, '%Y-%c-%d %H:%i:%s') as "createTime", date_format(no.update_time, '%Y-%c-%d %H:%i:%s') as "updateTime", no.remark as "remark", ptb.dic_code as "orderTypeCode", ptb.dic_name as "orderTypeName", os.dic_code as "orderStatusCode", os.dic_name as "orderStatusName" from n_order no INNER JOIN n_shop ns on ns.id=no.shop_id INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_dic ptb on no.order_type=ptb.dic_code and ptb.dic_type="orderType" LEFT JOIN n_food nf on nf.id in (no.foods_id) where no.order_type in ('payTheBill', 'payTheBillForMenu')
and no.id = #{id}
and no.uid = #{uid}
and no.status = #{status}
and express_number != "" and express_number IS NOT NULL
ORDER by no.create_time desc
LIMIT ${start},${size}

SELECT COUNT(*) total from n_order no INNER JOIN n_shop ns on ns.id=no.shop_id INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_dic ptb on no.order_type=ptb.dic_code and ptb.dic_type="orderType" LEFT JOIN n_food nf on nf.id in (no.foods_id) where no.order_type in ('payTheBill', 'payTheBillForMenu')
and no.id = #{id}
and no.uid = #{uid}
and no.status = #{status}
and express_number != "" and express_number IS NOT NULL
