        AND no.id = #{id}


        AND no.uid = #{uid}


        AND no.wx_order_id = #{wxOrderId}


        AND no.order_type = #{orderType}


        AND no.product_id = #{productId}


        AND no.product_num = #{productNum}


        AND no.address_id = #{addressId}


        AND no.express_number = #{expressNumber}


        AND no.shop_id = #{shopId}


        AND no.all_pay_amount = #{allPayAmount}


        AND no.pay_money = #{payMoney}


        AND no.use_balance_monney = #{useBalanceMonney}


        AND no.use_integral_num = #{useIntegralNum}


        AND no.status = #{status}


ORDER BY no.create_time desc

mapper.select:
select np.id as "productId", np.title as "productTitle", np.head_img_url as "productHeadImgUrl", np.price as "productPrice", no.product_num as "productNum", no.pay_money as "payMoney", no.express_number as "expressNumber", os.dic_name as "orderStatus" from n_order no INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_product np on no.product_id=np.id where
and no.uid = #{uid}
LIMIT ${start},${size}

select np.id as "productId", np.title as "productTitle", np.head_img_url as "productHeadImgUrl", np.price as "productPrice", no.id as "orderId", no.product_num as "productNum", no.all_pay_amount as "allPayAmount", no.pay_money as "payMoney", no.use_balance_monney as "useBalanceMonney", no.use_integral_num as "useIntegralNum", no.express_name as "expressName", no.express_number as "expressNumber", no.wx_order_id as "wxOrderId", date_format(no.create_time, '%Y-%c-%d %H:%i:%s') as "createTime", date_format(no.update_time, '%Y-%c-%d %H:%i:%s') as "updateTime", os.dic_code as "orderStatusCode", os.dic_name as "orderStatusName" from n_order no INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_product np on no.product_id=np.id where order_type='purchaseProduct'
and no.id = #{id}
and no.uid = #{uid}
and no.status = #{status}
and express_number != "" and express_number IS NOT NULL
ORDER by no.create_time desc
LIMIT ${start},${size}

SELECT COUNT(*) total from n_order no INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_product np on no.product_id=np.id where order_type='purchaseProduct'
and no.uid = #{uid}
and no.status = #{status}
