mapper.sql:


        AND no.status = #{status}


        AND no.id = #{id}


        AND no.uid = #{uid}


        AND no.wx_order_id = #{wxOrderId}


        AND no.shop_id = #{shopId}


        AND no.product_id = #{productId}


        AND no.product_num = #{productNum}


        AND no.address_id = #{addressId}


        AND no.use_balance_monney = #{useBalanceMonney}


        AND no.use_integral_num = #{useIntegralNum}


        AND no.pay_money = #{payMoney}


        AND no.pay_integral = #{payIntegral}


        AND no.order_type = #{orderType}


ORDER BY no.create_time desc

mapper.select:
select np.id as "productId", np.title as "productTitle", np.head_img_url as "productHeadImgUrl", np.price as "productPrice", no.product_num as "productNum", no.pay_money as "payMoney", no.express_number as "expressNumber", os.dic_name as "orderStatus" from n_order no INNER JOIN n_dic os on no.status=os.dic_code and os.dic_type="orderStatus" INNER JOIN n_product np on no.product_id=np.id where
and no.uid = #{uid}
LIMIT ${start},${size}

select nld.status as "status", count(nld.status) as "total" from n_luck_draw nld WHERE
nld.uid = #{uid}
GROUP BY nld.status
